package com.vampire.repository;import com.vampire.dataobject.ProductInfo;import lombok.extern.slf4j.Slf4j;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.math.BigDecimal;import java.util.List;import static org.junit.Assert.*;@RunWith(SpringRunner.class)@SpringBootTest@Slf4jpublic class ProductInfoRepositoryTest {    @Autowired    private ProductInfoRepository repository;    @Test    public void save() {        ProductInfo productInfo = new ProductInfo();        productInfo.setProductId("1234");        productInfo.setProductName("商品2");        productInfo.setProductPrice(new BigDecimal(5.8));        productInfo.setProductStock(16);        productInfo.setProductStatus(0);        productInfo.setProductDescription("商品介绍2");        productInfo.setProductIcon("http://xxx.jpg");        productInfo.setCategoryType(1);        ProductInfo result = repository.save(productInfo);        System.out.println(result);    }    @Test    public void findByProductStatusTest() {        List<ProductInfo> productInfoList = repository.findByProductStatus(0);        Assert.assertNotEquals(0, productInfoList.size());    }}